import axios from 'axios';
import React, { Component, CustomComponent }  from 'react';
import myApiService from "../api/apiService";
import ImageInput from 'react';
class FileUpload extends Component {

    state = {

        // Initially, no file is selected
        selectedFile: null
    };

    // On file select (from the pop up)
    onFileChange = event => {

        // Update the state
        this.setState({ selectedFile: event.target.files[0] });

    };

    // On file upload (click the upload button)
    onFileUpload = () => {
        console.log(this.props.id)
        console.log("State")

        console.log(this.state)

        // Create an object of formData
        const formData = new FormData();
        const config = {
            headers: {
                'content-type': 'multipart/form-data',
                'Accept': 'multipart/form-data'
            }
        }
        // Update the formData object
        formData.append(
            "file",
            this.state.selectedFile,
            this.state.selectedFile.name


    );

        // Details of the uploaded file
        console.log(this.state.selectedFile);
        console.log(this.props.id);

        console.log(this.props);

        // Request made to the backend api
        // Send formData object
        myApiService.uploadPic(formData, this.state.selectedFile.name, config, this.props.id)
    };

    // File content to be displayed after
    // file upload is complete
    fileData = () => {

        if (this.state.selectedFile) {

            return (
                <div>
                    <h2>File Details:</h2>
                    <p>File Name: {this.state.selectedFile.name}</p>
                    <p>File Type: {this.state.selectedFile.type}</p>
                    <p>
                        Last Modified:{" "}
                        {this.state.selectedFile.lastModifiedDate.toDateString()}
                    </p>
                    <img  width="500" height="600" src={"http://o.ssystems.de/images/" + this.state.selectedFile.name}/>

                </div>
            );
        } else {
            return (
                <div>
                    <br />
                    <h4>Choose before Pressing the Upload button</h4>
                </div>
            );
        }
    };

    render() {

        return (
            <div>
                <h1>
                    Upload Pic
                </h1>
                <h3>
                    File Upload using React!
                </h3>
                <div>
                    <input type="file" onChange={this.onFileChange} />
                    <button onClick={this.onFileUpload}>
                        Upload!
                    </button>
                </div>
                {this.fileData()}

            </div>
        );
    }
}

export default FileUpload;
