import * as React from 'react';
import {FC} from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import {ReferenceField, useShowController, Datagrid, TextField,List,  ReferenceManyField,
    SingleFieldList,
    ChipField,} from 'react-admin';
import Paper from '@material-ui/core/Paper';
import {Tbl} from "./comp/table";


const useStyles = makeStyles({
    container: {minWidth: '35em', marginLeft: '1em'},
    rightAlignedCell: {textAlign: 'right'},
    boldCell: {fontWeight: 'bold'},
    root: {width: 1400 , margin: '10px', },
    spacer: {height: 20},
});


const UserShow = (props) => {
    const {record} = useShowController(props);
    const classes = useStyles();

    if (!record) return null;
    return (
        <Card className={classes.root}>
            <CardContent className='userinfo'>
                <Grid container spacing={2}>
                    <Grid item xs={6}>
                        <Typography variant="h6" gutterBottom>
                            User Information
                        </Typography>
                    </Grid>

                </Grid>
                <Grid container spacing={2}>
                    <Grid item xs={12} container alignContent="flex-end">
                        <ReferenceField
                            reference="student"
                            source="student"
                            basePath="/student"
                            record={record}
                            link={false}
                        >
                            <Typography className='userdata'>
                                 {record.addressid}
                                <br/>
                                {record.anrede}
                                <br/>
                                {record.surname} {record.givenname}
                                <br/>

                            </Typography>

                        </ReferenceField>
                    </Grid>
                </Grid>
<Paper>
<Tbl addressid={props.id}/>
</Paper>
            </CardContent>
        </Card>
    );
};

export default UserShow;


