import React, {cloneElement, useState} from 'react';
import {
    ChipField,
    Datagrid,
    Edit,
    EditButton,
    List,
    ReferenceField,
    ReferenceManyField,
    SimpleForm,
    SingleFieldList,
    TextField,
    TextInput,
    SearchInput,
    Filter,
    Pagination, Button

} from 'react-admin';
import {RoleListBox} from './comp/rolelistbox';
import './comp/comp.css'
import Box from "@material-ui/core/Box";
import UserShow from "./usershow3";
import {MyEdit} from "./myEdit";




const ProfilPagination = props => <Pagination rowsPerPageOptions={[10, 25, 50, 100]} {...props} />;

const ProfilFilter = (props) => (
    <Filter {...props}>

        <SearchInput source="surname" alwaysOn/>
        <SearchInput source="givenname"/>
        <SearchInput source= "gender" />
        <SearchInput source="termconfiguration"/>
        <SearchInput source="addressid"/>
        <SearchInput source="anrede" />

    </Filter>
);
/**
 *
 * editing specified user.
 *
 * @param props
 * @returns {*}
 * @constructor
 */


const [count, setCount] = useState(0);

const WrappedSimpleForm = ({save, ...rest}) => {
    const mySave = (event) => {
        alert("Was passiert hier")
        setCount(count + 1)
        alert(count)
};
    return (<SimpleForm save={mySave} {...rest} />);
}

export const ProfilEdit = (props) => (

    <MyEdit  {...props}>
        <WrappedSimpleForm>
            <div style={{width: '100%'}}>
                <Box display="flex" flexDirection="row" p={1} m={1} bgcolor="background.paper">
                    <Box width="40%" bgcolor="white.300">
                        <TextInput label="resources.students.fields.anrede" disabled source="anrede"/>
                        <TextInput label="resources.students.fields.surname" disabled source="surname"/>
                        <TextInput label="resources.students.fields.givenname" disabled source="givenname"/>
                        <TextInput label="resources.students.fields.gender"  source="gender"/>
                        <TextInput label="resources.students.fields.termconfiguration" disabled source="termconfiguration"/>
                    </Box>
                    <Box width="60%" bgcolor="white.300">
                        <RoleListBox id_user={props.id}/>
                    </Box>
                </Box>
            </div>
        </WrappedSimpleForm>
    </MyEdit>
);


/**
 *
 * @param props
 * @returns {*}
 * @constructor
 */


export const ProfilList = (props) => (


        <List
            {...props}
            pagination={<ProfilPagination/>}
            filters={ <ProfilFilter /> }
            sort={{field: 'id', order: 'DESC'}}>

            <Datagrid rowClick="expand" expand={ <UserShow />}>
                <TextField label="AddressId" source="id"/>
                <TextField source="anrede"/>
                <TextField source="surname"/>
                <TextField source="givenname"/>
                <TextField source="gender"/>
                <TextField source="termconfiguration"/>
                <ReferenceManyField label='pos.role' reference="user_role" target="id_user">
                    <SingleFieldList linkType={false}>
                        <ReferenceField source="id_role" reference="role">
                            <ChipField source="name"/>
                        </ReferenceField>
                    </SingleFieldList>
                </ReferenceManyField>
                <EditButton/>
            </Datagrid>
        </List>


    );
