import * as React from 'react';
import {FC} from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import {ReferenceField, useShowController, Datagrid, TextField,List} from 'react-admin';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
    container: {minWidth: '35em', marginLeft: '1em'},
    rightAlignedCell: {textAlign: 'right'},
    boldCell: {fontWeight: 'bold'},
    root: {width: 1200, margin: 'auto'},
    spacer: {height: 20},
});


const UserShow = (props: any) => {
    const {record} = useShowController(props);
    const classes = useStyles();

    if (!record) return null;
    return (
        <Card className={classes.root}>
            <CardContent className='userinfo'>
                <Grid container spacing={2}>
                    <Grid item xs={6}>
                        <Typography variant="h6" gutterBottom>
                            User Information
                        </Typography>
                    </Grid>

                </Grid>
                <Grid container spacing={2}>
                    <Grid item xs={12} container alignContent="flex-end">
                        <ReferenceField
                            reference="user_all"
                            source="user_all"
                            basePath="/user_all"
                            record={record}
                            link={false}
                        >
                            <Typography className='userdata'>
                                 {record.addressid}
                                <br/>
                                {record.anrede}
                                <br/>
                                {record.surname} {record.givenname}
                                <br/>

                            </Typography>

                        </ReferenceField>
                    </Grid>
                </Grid>
                <div className={classes.spacer}>&nbsp;</div>
                <Grid container spacing={2}>
                    <Grid item xs={6}>
                        <Typography variant="h6" gutterBottom align="center">
                            immatdate{' '}
                        </Typography>
                        <Typography gutterBottom align="center">
                            {new Date(record.immatdate).toLocaleDateString()}
                        </Typography>
                    </Grid>
                    <Grid item xs={5}>
                        <Typography variant="h6" gutterBottom align="center">
                            exmatdate{' '}
                        </Typography>
                        <Typography gutterBottom align="center">
                            {new Date(record.exmatdate).toLocaleDateString()}
                        </Typography>
                    </Grid>
                </Grid>
<Paper>
    <Table>
        <TableHead>
            <TableRow>
                <TableCell>
                    termconfiguration
                </TableCell>
                <TableCell>
                    gender
                </TableCell>
                <TableCell>
                    exmatreason
                </TableCell>
                <TableCell>
                    degreecode
                </TableCell>
                <TableCell>
                    degree
                </TableCell>
                <TableCell>
                    university
                </TableCell>
                <TableCell>
                    capr_nameeng
                </TableCell>
                <TableCell>
                    capr_degreeshort
                </TableCell>
                <TableCell>
                    isnotifiable
                </TableCell>
                <TableCell>
                    rwte_name
                </TableCell>
                <TableCell>
                    term_type
                </TableCell>
            </TableRow>
        </TableHead>
        <TableBody>
            <TableRow>
                <TableCell>
                    {record.termconfiguration}
                </TableCell>
                <TableCell>
                    {record.gender}
                </TableCell>
                <TableCell>
                    {record.exmatreason}
                </TableCell>
                <TableCell>
                    {record.degreecode}
                </TableCell>
                <TableCell>
                    {record.degree}
                </TableCell>
                <TableCell>
                    {record.university}
                </TableCell>
                <TableCell>
                    {record.capr_nameeng}
                </TableCell>
                <TableCell>
                    {record.capr_degreeshort}
                </TableCell>
                <TableCell>
                    {record.isnotifiable}
                </TableCell>
                <TableCell>
                    {record.rwte_name}
                </TableCell>
                <TableCell>
                    {record.term_type}
                </TableCell>
            </TableRow>
        </TableBody>
    </Table>
</Paper>
            </CardContent>
        </Card>
    );
};

export default UserShow;


