import React,{ Fragment }  from 'react';
import {ChipField, Create, Datagrid, NumberInput, Edit, EditButton, List, ReferenceField, ReferenceManyField, SimpleForm, SingleFieldList, TextField, TextInput} from 'react-admin';
import {RightListBox} from "./comp/rightlistbox";
import apiService from './api/apiService';
// in src/posts.js

/**
 *
 * creating a new role.
 *
 * @param props
 * @returns {*}import apiService from '../api/apiService';
 * @constructor
 */
export const RoleCreate = props => (
    <Create {...props}>
        <SimpleForm>
            <TextInput source="name" validate={required()}/>

        </SimpleForm>
    </Create>

);



const validateRoleCreation = (values) => {
    const errors = {};
    if (!values.name) {
        errors.firstName = ['The Name is required'];
    }

    let result = apiService.fetchRoleObjectForName(values.name);
    console.log("Result of fetching Role : " + result);
    if (result) {
        errors.age = ['The name already exists'];
    } else if (values.age < 18) {
        errors.age = ['Must be over 18'];
    }
    return errors
};

/**
 *
 * showing list of the role.
 *
 * @param props
 * @returns {*}
 * @constructor
 */
export const RoleList = (props) => (
    <Fragment>
        <List {...props}>

            <Datagrid>
                <TextField source="id" />
                <TextField source="name" />
                /*<ReferenceManyField label="Right" reference="role_right" target="id_role">
                    <SingleFieldList linkType={false}>
                        <ReferenceField source="id_right" reference="right">
                            <ChipField source="name" />
                        </ReferenceField>
                    </SingleFieldList>
                </ReferenceManyField>*/
                <EditButton />
            </Datagrid>
        </List>
    </Fragment>
);

/**
 *
 * editing a specified role.
 *
 * @param props
 * @returns {*}
 * @constructor
 */
export const RoleEdit = props => (
    <Edit {...props}>
        <SimpleForm >
            <TextInput source="name" validate={required()}/>
            <RightListBox id_role={props.id}/>
        </SimpleForm>
    </Edit>

);
