import React, {cloneElement, useState} from 'react';
import {
    ChipField,
    Datagrid,
    Edit,
    EditButton,
    List,
    ReferenceField,
    ReferenceManyField,
    SimpleForm,
    SingleFieldList,
    TextField,
    TextInput,
    SearchInput,
    Filter,
    Pagination, Button

} from 'react-admin';
import {RoleListBox} from './comp/rolelistbox';
import './comp/comp.css'
import Box from "@material-ui/core/Box";
import UserShow from "./usershow3";
import {MyEdit} from "./myEdit";




const QuestionPagination = props => <Pagination rowsPerPageOptions={[10, 25, 50, 100]} {...props} />;

const QuestionFilter = (props) => (
    <Filter {...props}>

        <SearchInput source="name" alwaysOn/>
        <SearchInput source="detail" alwaysOn/>
    </Filter>
);
/**
 *
 * editing specified user.
 *
 * @param props
 * @returns {*}
 * @constructor
 */

const WrappedSimpleForm = ({save, ...rest}) => {
    const mySave = () => {alert("Alles cscheisse")};
    return (<SimpleForm save={mySave} {...rest} />);
}

export const QuestionEdit = (props) => (

    <MyEdit  {...props}>
        <WrappedSimpleForm>
            <div style={{width: '100%'}}>
                <Box display="flex" flexDirection="row" p={1} m={1} bgcolor="background.paper">
                    <Box width="40%" bgcolor="white.300">
                        <TextInput label="ID" disabled source="id"/>
                        <TextInput label="Text" source="text"/>
                        <TextInput label="Sprache" disabled source="language"/>
                        <TextInput label="Detail"  source="detail"/>
                    </Box>
                    <Box width="60%" bgcolor="white.300">
                        <RoleListBox id_user={props.id}/>
                    </Box>
                </Box>
            </div>
        </WrappedSimpleForm>
    </MyEdit>
);


/**
 *
 * @param props
 * @returns {*}
 * @constructor
 */


export const QuestionList = (props) => (


        <List
            {...props}
            pagination={<QuestionPagination/>}
            filters={ <QuestionFilter /> }
            sort={{field: 'id', order: 'DESC'}}>

            <Datagrid>
                <TextField label="ID" source="id"/>
                <TextField source="text"/>
                <TextField source="language"/>
                <TextField source="details"/>
                <EditButton/>
            </Datagrid>
        </List>


    );
